INSERT INTO `game_config_files` (`game_id`, `file_id`, `mod_id`, `relative_path`, `description`, `enable_text_editor_reseller`, `enable_text_editor_user`, `view_order`, `template`, `enable_config_editor`, `read_values`, `hide_path`, `app_data`, `hide_read_failure`, `update_automatically`) VALUES
(5, 7, 0, 'etmain\\server.cfg', 'Main configuration file', NULL, NULL, 1, '// PASSWORDS\nset g_password \"$[PrivatePassword]\"      // set to password protect the server \nset sv_privateclients \"![PrivateClients]\"                // if set > 0, then this number of client slots will be reserved for connections \nset sv_privatepassword \"![PrivateClientsPassword]\"                // that have \"password\" set to the value of \"sv_privatePassword\" \nset rconpassword \"$[RConPassword]\"       // remote console access password \nset refereePassword \"![RefereePassword]\"    // referee status password \n\n// DL, RATE \n\nset sv_maxRate \"![MaxRate]\"                   // 10000 standard but poor for ET \nset sv_dl_maxRate \"![DownloadMaxRate]\"                // increase/decerease if you have plenty/little spare bandwidth \nset sv_allowDownload \"![AllowDownload]\"                 // global toggle for both legacy download and web download \nset sv_wwwDownload \"$[FastDownloadEnabled]\"                   // toggle to enable web download \nset sv_wwwBaseURL \"$[FastDownloadUrl]\"                     // base URL for redirection \nset sv_wwwDlDisconnected \"![WebDownloadDisconnected]\"             // tell clients to perform their downloads while disconnected from the server \nset sv_wwwFallbackURL \"![WebFallbackUrl]\"                 // URL to send to if an http/ftp fails or is refused client side \n\n// MOTD ETC \n\nset sv_hostname \"$[HostName]\"            // name of server here \nset server_motd0 \"![MOTD0]\"   // message in right corner of join screen here \nset server_motd1 \"![MOTD1]\" \nset server_motd2 \"![MOTD2]\" \nset server_motd3 \"![MOTD3]\" \nset server_motd4 \"![MOTD4]\" \nset server_motd5 \"![MOTD5]\" \n\n// MISC SETTINGS \n\nset g_heavyWeaponRestriction \"100\" \nset g_antilag \"1\" \nset g_altStopwatchMode \"0\" \nset g_autofireteams \"1\" \nset g_complaintlimit \"6\" \nset g_ipcomplaintlimit \"3\" \nset g_fastres \"0\" \nset g_friendlyFire \"1\" \n//set g_gametype \"4\"            // Game type should be set from map rotation script \nset g_minGameClients \"8\" \nset g_maxlives \"0\" \nset g_alliedmaxlives \"0\" \nset g_axismaxlives \"0\" \nset g_teamforcebalance \"1\" \nset g_noTeamSwitching \"0\" \nset g_voiceChatsAllowed \"4\" \nset g_doWarmup \"0\" \nset g_warmup \"30\" \nset g_spectatorInactivity \"0\" \nset sv_floodProtect \"1\" \nset sv_allowDownload \"1\" \nset sv_pure \"1\" \nset sv_minping \"0\" \nset sv_maxping \"0\" \nset match_latejoin \"1\" \nset match_minplayers \"4\" \nset match_mutespecs \"0\" \nset match_readypercent \"100\" \nset match_timeoutcount \"0\" \nset match_warmupDamage \"1\" \nset team_maxplayers \"0\" \nset team_nocontrols \"1\" \nset pmove_fixed \"0\" \nset pmove_msec \"8\" \n\n// LMS ONLY SETTINGS \n\nset g_lms_teamForceBalance \"1\" \nset g_lms_roundlimit \"3\" \nset g_lms_matchlimit \"2\" \nset g_lms_currentMatch \"0\" \nset g_lms_lockTeams \"0\" \nset g_lms_followTeamOnly \"1\" \n\n// VOTING \n\nset g_allowVote \"1\" \nset vote_limit \"5\" \nset vote_percent \"50\" \nset vote_allow_comp \"1\" \nset vote_allow_gametype \"1\" \nset vote_allow_kick \"1\" \nset vote_allow_map \"1\" \nset vote_allow_matchreset \"1\" \nset vote_allow_mutespecs \"1\" \nset vote_allow_nextmap \"1\" \nset vote_allow_pub \"1\" \nset vote_allow_referee \"0\" \nset vote_allow_shuffleteams \"1\" \nset vote_allow_swapteams \"1\" \nset vote_allow_friendlyfire \"1\" \nset vote_allow_timelimit \"0\" \nset vote_allow_warmupdamage \"1\" \nset vote_allow_antilag \"1\" \nset vote_allow_balancedteams \"1\" \nset vote_allow_muting \"1\" \n\n// PUNKBUSTER \n\n// sv_punkbuster is a readonly variable. Use +set sv_punkbuster on command line or use the command pb_sv_enable in the console \nexec punkbuster.cfg\n\n// LOGGING \n\nset g_log \"etserver.log\"          // Game logging \nset g_logsync 1 \nset logfile 2                // Console logging ( 1: enable 2: enable and sync ) \n\n// MAP ROTATION \nexec ![MapRotationCfg]\n\n// WATCHDOG \n\n// in case the game dies with an ERR_DROP \n// or any situation leading to server running with no map \n//set com_watchdog 10            // defaults 60 \nset com_watchdog_cmd \"exec ![MapRotationCfg]\"   // defaults to quit \n', 1, 1, 0, '<?xml version=\"1.0\" encoding=\"utf-16\" standalone=\"yes\"?>\r\n<values>\r\n  <add key=\"__TCA:NOTES\" value=\"\" type=\"System.String,mscorlib\" />\r\n  <add key=\"__TCA:MODIFIED_BY\" value=\"3\" type=\"System.Int32,mscorlib\" />\r\n  <add key=\"__TCA:MODIFIED_ON\" value=\"2017-05-13 21:34:24\" type=\"System.DateTime,mscorlib\" />\r\n</values>', NULL, 1),
(5, 6, 0, 'etmain\\punkbuster.cfg', 'Punkbuster configuration file', 1, 1, 2, '//PunkBuster Server config \n//\n//Thanks to those on the SplashDamage forums for their contribution.\n//Compiled by Erik Melen (Deveus) and Paul Goldilla (loony)\n//\n//Enable Punkbuster\n![EnablePunkbuster]\n\n\n//Bad Names list\n//Place any names that you feel players should not use on your server in here.\n//To add more bad name entries, simply follow the format shown below.\npb_sv_badname 30 Nazi \npb_sv_badname 30 Hitler\n\n\n//CVARs for the PB server to check. Place checks to keep players from abusing CVARs.\npb_sv_cvarempty \n\npb_sv_cvar rate in 2500 25000 \npb_sv_cvar snaps in 20 40 \npb_sv_cvar cl_maxpackets in -15 100 \npb_sv_cvar cg_bobup in 0 0.005 \npb_sv_cvar r_picmip in 0 3\npb_sv_cvar r_overbrightbits in 0 4 \npb_sv_cvar r_mapoverbrightbits in 0 4 \npb_sv_cvar cg_shadows in 0 1 \npb_sv_cvar r_rmse in 0 0 \npb_sv_cvar cg_fov in 90 120 \npb_sv_cvar r_allowextensions in 1\npb_sv_cvar r_softwareGL in 0\npb_sv_scorekick -9     //player gets kicked for having score below zero\n\n\npb_sv_CQC 1 //Client Query Capability (i.e. can they query the server cvars)\n\npb_sv_MsgPrefix \"ET PB Server\" \npb_sv_KickLen 5 \npb_sv_LogSync 0 \npb_sv_LogCeiling 1000 \npb_sv_Sleep 100 \npb_sv_PowerMin 10 \npb_sv_PowerDef 1 \npb_sv_PowerKickLen 5 \n//pb_sv_httpAddr 192.168.0.1  //enter Webtool address here\n//pb_sv_httpPort 27970        //enter Webtool port here\n//pb_sv_httpRefresh 60 \n//pb_sv_httpKey \"Put Password Here\" \n//pb_sv_httpmaps radar railgun fueldump oasis battery goldrush   //for the Web Tool map pull down menu\n\npb_sv_extChar 0 \npb_sv_minName 1 \npb_sv_dupNameGrace 30 //Prevents name stealing on the server.\npb_sv_changePeriod 10 \npb_sv_changeMax 5 //Number of name changes before kicking a player.\npb_sv_emptyname 1', 1, 0, 0, '<?xml version=\"1.0\" encoding=\"utf-16\" standalone=\"yes\"?>\r\n<values>\r\n  <add key=\"__TCA:NOTES\" value=\"\" type=\"System.String,mscorlib\" />\r\n  <add key=\"__TCA:MODIFIED_BY\" value=\"3\" type=\"System.Int32,mscorlib\" />\r\n  <add key=\"__TCA:MODIFIED_ON\" value=\"2010-10-05 05:55:05\" type=\"System.DateTime,mscorlib\" />\r\n</values>', NULL, 1),
(5, 2, 0, 'etmain\\campaigncycle.cfg', 'Campaign cycle configuration', 1, 1, 3, '![CampaignCycle]\n\n// server doesn\'t recognise the campaign command when the gamecode isn\'t running yet.\nset d_initial \"set g_gametype 4 ; map ![CampaignCycleInitialMap] ; set nextcampaign vstr d2\"\nvstr d_initial', 1, 1, 0, '<?xml version=\"1.0\" encoding=\"utf-16\" standalone=\"yes\"?>\r\n<values>\r\n  <add key=\"__TCA:NOTES\" value=\"\" type=\"System.String,mscorlib\" />\r\n  <add key=\"__TCA:MODIFIED_BY\" value=\"3\" type=\"System.Int32,mscorlib\" />\r\n  <add key=\"__TCA:MODIFIED_ON\" value=\"2013-05-13 23:06:25\" type=\"System.DateTime,mscorlib\" />\r\n</values>', NULL, 1),
(5, 5, 0, 'etmain\\stopwatchcycle.cfg', 'Stopwatch cycle configuration', 1, 1, 4, '//Map-Cycle Stopwatch\n//by Erik Melen (Deveus)\n//\n//3 = Stopwatch\n//Stopwatch Rotation. A full ABBA or ABAB rotation requires 4 rounds per map.\n//A simple map_restart is all that is needed for rounds 2-4.\n\n![StopwatchCycle]\n\nvstr m2r1', 1, 1, 0, '<?xml version=\"1.0\" encoding=\"utf-16\" standalone=\"yes\"?>\r\n<values>\r\n  <add key=\"__TCA:NOTES\" value=\"\" type=\"System.String,mscorlib\" />\r\n  <add key=\"__TCA:MODIFIED_BY\" value=\"3\" type=\"System.Int32,mscorlib\" />\r\n  <add key=\"__TCA:MODIFIED_ON\" value=\"2010-10-05 05:55:23\" type=\"System.DateTime,mscorlib\" />\r\n</values>', NULL, 1),
