(28, 1, 0, 'main\\server.cfg', '', NULL, NULL, NULL, '// This config is set to SD via the map rotation. If you would like to change to another gametype, use one of the map rotations at the bottom of this config. \r\n\r\n// ************************************************** *** \r\n// *********** Server Security & Errors *************\r\n// ************************************************** *** \r\n\r\n//Passwords \r\nset rcon_password \"$[RConPassword]\" //CHANGE ME \r\nset sv_privatePassword \"$[PrivatePassword]\" // OPTIONAL CHANGE ME \r\nset g_password \"$[PrivatePassword]\" // Only used if you want to close your server to the public \r\n\r\n//Logging \r\nset g_log \"server_mp.log\" \r\nset g_logSync \"1\" \r\n\r\n//SV Pure \r\nset sv_pure \"1\" \r\n\r\n//SV Cheats \r\nset sv_cheats \"0\" \r\n\r\n//Allowed Clans \r\nset sv_allowedClan1 \"\" \r\nset sv_allowedClan2 \"\" \r\n\r\n//Disable Console \r\nset sv_disableClientConsole \"0\" \r\n\r\nset sv_reconnectlimit \"3\" \r\n\r\n// ************************************************** *** \r\n// ************ Public Info **************************** \r\n// ************************************************** *** \r\n\r\n// SETS/PUBLIC INFO (stuff that shows up in server info) \r\n\r\nsets g_needpass \"$[IsPrivate]\" // Denotes whether the server is public or private \r\n//sets IRC \"\" // your IRC channel + server here (optional) \r\n//sets Admin \"\" // Make it your IRC or site nick maybe (optional) \r\n//sets Email \"\" // You\'re email address (optional) \r\n//sets URL \"\" // your website here (optional)\r\n\r\n// ************************************************** *** \r\n// *********** Server Dvars              **************** \r\n// ************************************************** *** \r\n\r\n// Hostname and MOTD \r\nsv_hostname \"$[HostName]\" \r\nset scr_motd \"Welcome\" \r\n\r\n// Reserved Slots \r\nset sv_privateClients \"\" //Allows players to enter the server without taking up public slots \r\n\r\n//Downloads \r\nseta sv_allowAnonymous \"0\" \r\nset sv_allowdownload \"1\" \r\n\r\n// Server Packet Rate \r\nset sv_maxRate \"25000\" \r\n\r\n// Limit Laggers and reconnect spammers \r\nset sv_minPing \"0\" \r\nset sv_maxPing \"250\" \r\n\r\n// - Banning \r\nset g_banIPs \"\" \r\n\r\n// AntiFlooding Settings \r\nset sv_floodProtect \"1\" \r\n\r\n// ************************************************** *** \r\n// ******************** Voice ************************* \r\n// ************************************************** *** \r\n\r\nset sv_voice \"0\" \r\n\r\nset sv_voiceQuality \"1\" \r\n\r\n// Match Setup Options\r\n// Use these to set how your server will play a map. \r\n\r\n// CTF \r\nset scr_ctf_scorelimit \"5\" \r\nset scr_ctf_timelimit \"30\" \r\n\r\n// S&D \r\nset scr_sd_bombtimer \"60\" \r\nset scr_sd_graceperiod \"15\" \r\nset scr_sd_roundlength \"4\" \r\nset scr_sd_roundlimit \"0\" \r\nset scr_sd_scorelimit \"10\" \r\nset scr_sd_timelimit \"0\" \r\n\r\n// TDM \r\nset scr_tdm_scorelimit \"300\" \r\nset scr_tdm_timelimit \"30\" \r\n\r\n// DM \r\nset scr_dm_scorelimit \"50\" \r\nset scr_dm_timelimit \"30\" \r\n\r\n// HQ \r\nset scr_hq_scorelimit \"600\" \r\nset scr_hq_timelimit \"30\" \r\n\r\n// ************************************************** *** \r\n// ***************** Gameplay Dvars ****************\r\n// ************************************************** *** \r\n\r\n// Team Icons \r\nset scr_drawfriend \"1\" \r\n\r\n// Friendly Fire \r\nset scr_friendlyfire \"0\" \r\n\r\n// Kill Cam & Spectate \r\nset scr_killcam \"0\" \r\nset scr_spectatefree \"0\" \r\nset scr_spectateenemy \"0\" \r\n\r\n// Auto Team Balance \r\nset scr_teambalance \"1\" \r\n\r\n// Force Respawn \r\nset scr_forcerespawn \"1\" \r\n\r\n// ************************************************ \r\n// ******************* Voting ********************* \r\n// ************************************************ \r\n\r\nset g_allowvote \"0\" \r\n\r\n// ************************************************** *** \r\n// ************ Weapon Settings ************************ \r\n// ************************************************** *** \r\n\r\n// Weapon Ammo Pools \r\nset g_weaponAmmoPools \"0\" \r\n\r\n//Snipers \r\nset scr_allow_enfieldsniper \"1\" \r\nset scr_allow_kar98ksniper \"1\" \r\nset scr_allow_nagantsniper \"1\" \r\nset scr_allow_springfield \"1\" \r\nset scr_allow_svt40 \"1\" \r\n\r\n// Rifles \r\nset scr_allow_enfield \"1\" \r\nset scr_allow_kar98k \"1\" \r\nset scr_allow_m1carbine \"1\" \r\nset scr_allow_m1garand \"1\" \r\nset scr_allow_nagant \"1\" \r\nset scr_allow_g43 \"1\" \r\n\r\n// MGs \r\nset scr_allow_bar \"1\" \r\nset scr_allow_bren \"1\" \r\nset scr_allow_mp44 \"1\" \r\nset scr_allow_ppsh \"1\" \r\n\r\n//SMGs \r\nset scr_allow_sten \"1\" \r\nset scr_allow_mp40 \"1\" \r\nset scr_allow_thompson \"1\" \r\nset scr_allow_pps42 \"1\" \r\nset scr_allow_greasegun \"1\" \r\n\r\n// Shotgun \r\nset scr_allow_shotgun \"1\" \r\n\r\n// Grenades \r\nset scr_allow_smokegrenades \"1\" \r\nset scr_allow_fraggrenades \"1\" \r\n\r\n// Below is a listing of Map Rotations for each gametype, along with gametype settings.\r\n\r\n//Map Rotations\r\n\r\n//TDM: \r\n//set sv_mapRotation \"gametype tdm map mp_breakout gametype tdm map mp_brecourt gametype tdm map mp_burgundy gametype tdm map mp_carentan gametype tdm map mp_dawnville gametype tdm map mp_decoy gametype tdm map mp_downtown gametype tdm map mp_farmhouse gametype tdm map mp_leningrad gametype tdm map mp_matmata gametype tdm map mp_railyard gametype tdm map mp_toujane gametype tdm map mp_trainstation\" \r\n\r\n//CTF: \r\n//set sv_mapRotation \"gametype ctf map mp_breakout gametype ctf map mp_brecourt gametype ctf map mp_burgundy gametype ctf map mp_carentan gametype ctf map mp_dawnville gametype ctf map mp_decoy gametype ctf map mp_downtown gametype ctf map mp_farmhouse gametype ctf map mp_leningrad gametype ctf map mp_matmata gametype ctf map mp_railyard gametype ctf map mp_toujane gametype ctf map mp_trainstation\" \r\n\r\n//DM: \r\n//set sv_mapRotation \"gametype dm map mp_breakout gametype dm map mp_brecourt gametype dm map mp_burgundy gametype dm map mp_carentan gametype dm map mp_dawnville gametype dm map mp_decoy gametype dm map mp_downtown gametype dm map mp_farmhouse gametype dm map mp_leningrad gametype dm map mp_matmata gametype dm map mp_railyard gametype dm map mp_toujane gametype dm map mp_trainstation\" \r\n\r\n//HQ: \r\n//set sv_mapRotation \"gametype hq map mp_breakout gametype hq map mp_brecourt gametype hq map mp_burgundy gametype hq map mp_carentan gametype hq map mp_dawnville gametype hq map mp_decoy gametype hq map mp_downtown gametype hq map mp_farmhouse gametype hq map mp_leningrad gametype hq map mp_matmata gametype hq map mp_railyard gametype hq map mp_toujane gametype hq map mp_trainstation\" \r\n\r\n//SD: \r\nset sv_mapRotation \"gametype sd map mp_breakout gametype sd map mp_brecourt gametype sd map mp_burgundy gametype sd map mp_carentan gametype sd map mp_dawnville gametype sd map mp_decoy gametype sd map mp_downtown gametype sd map mp_farmhouse gametype sd map mp_leningrad gametype sd map mp_matmata gametype sd map mp_railyard gametype sd map mp_toujane gametype sd map mp_trainstation\"', NULL, 1, 0, '<?xml version=\"1.0\" encoding=\"utf-16\" standalone=\"yes\"?>\r\n<values>\r\n  <add key=\"__TCA:CREATED_BY\" value=\"3\" type=\"System.Int32,mscorlib\" />\r\n  <add key=\"__TCA:CREATED_ON\" value=\"2011-07-27 15:51:57\" type=\"System.DateTime,mscorlib\" />\r\n</values>', NULL, 1),
(29, 1, 0, 'main\\dedicated.cfg', 'Contains server configuration settings', NULL, NULL, 0, '//Call of Duty Default Server Configuration File\n\n\nseta sv_hostname \"$[HostName]\" // name of your server as listed in the in game browser\nseta g_privatepassword \"Your_Private_slot_password\" // set your private password here, works with sv_privateclients\nseta rconPassword \"$[RConPassword]\" // set your rcon password \nseta g_log \"server.log\" // what you wish to name your log file\nseta protocol \"1\" \nseta sv_pure \"1\"  // pure servers, will not allow anyone to play on your server with custom pk3 files loaded into their main dir.\nseta g_logsync \"1\" \nseta sv_gamespy \"1\" // 1 = your server will be listed on gamespy 0 = your server will not be listed on gamespy\nseta sv_floodprotect \"1\" \nseta g_forceteamspectate \"1\" \nseta sv_invulnerabletime \"3\" // time in seconds that you can not be killed when spawned\nseta g_teamkillwarn \"3\" // number of team kills before a warning is issued\nseta g_teamkillkick \"5\"  // number of team kills before client is kicked\nseta g_teamswitchdelay \"10\"\nseta sv_maxping \"250\" // max ping a client can have to connect to your server, only checked when client is joining\nseta sv_maxrate \"20000\" // bandwidth your server is allowing for each client. Please see other guide on how to calculate this\nset g_inactivity \"120\" //Set time before an inactive client is kicked\nseta scr_motd \"\" // this will be displayed on the server menu when clients join\nseta sv_privateclients \"\" // leave blank unless you wish to have private clients on your server.\nseta g_password \"$[PrivatePassword]\"  // game password if you want a private server\nseta g_allowvote \"0\" \n\n\n//General Game Default Deion \nseta scr_allow_bar \"1\" //0 - disable weapon 1 - enable weapon \nseta scr_allow_bren \"1\" \nseta scr_allow_enfield \"1\" \nseta scr_allow_fg42 \"1\" \nseta scr_allow_kar98k \"1\" \nseta scr_allow_kar98ksniper \"1\" \nseta scr_allow