(33, 1, 0, 'main\\server.cfg', 'MOH Server Configuration', NULL, NULL, NULL, '// Server Name\r\n// Name that will appear on the server browser\r\nsv_hostname \"$[HostName]\"\r\n\r\n// Private Clients\r\n// Clients that require a password to join\r\nset sv_privateclients 0\r\nset sv_privatepassword \"password_for_slots\"\r\n\r\n// Password\r\n// Allows you to set a password for the server\r\nset g_password \"$[PrivatePassword]\"\r\nsets g_needpass $[IsPrivate]\r\n\r\n// Game Type\r\n// 1 Free for All\r\n// 2 Team Based\r\n// 3 Round Based\r\n// 4 Objective\r\nset g_gametype 4\r\n\r\n// RCON Capability\r\n// Leave blank to disable rcon.\r\nset rconpassword $[RConPassword]\r\n\r\n// Health Drop\r\n// When enabled players will drop a health pack when killed\r\nset g_healthdrop 1\r\n\r\n// Fast Run Speed\r\n// When checked, the speed at which players can run through levels is increased. \r\n// Leaving it unchecked defaults to Medal of Honor Allied Assault\'s original run speed\r\nset sv_dmspeedmult 1.100000\r\n\r\n// Team Damage\r\n// Toggles friendly fire\r\nset g_teamdamage 0\r\n\r\n// Time Limit\r\n// This sets the elapsed time at which the game ends, the winner being the player with the highest score at that time.\r\ntimelimit 10\r\n\r\n// Frag Limit\r\n// The score at which the player or team will win the current map.\r\nfraglimit 100\r\n\r\n// Map Rotation Time\r\n// This sets the maximum amount of time in minutes players will spend in a particular map.\r\nmaprotationtime 30\r\n\r\n// Team Spawn Delay\r\n// This sets a delay in seconds between spawns. This is useful if you would like the \r\n// player spawned in batches, rather than one at a time\r\n//set sv_team_spawn_interval 15\r\n\r\n// Round Reset Time\r\n// This sets how long each round will take, in minutes. This can be smaller than the map rotation time, but not larger.\r\nroundlimit 0\r\n\r\n// Inactive Spectate\r\n// This sets the time in seconds after which an inactive player becomes a spectator. To save server bandwidth, 60 seconds is default.\r\nset g_inactivespectate 60\r\n\r\n// Inactive Kick\r\n// This sets the time in seconds after which an inactive player is kicked out of the game. 900 (15 minutes) seconds is default.\r\nset g_inactivekick 900\r\n\r\n// Use Gamespy\r\n// To use GameSpy gaming service for internet-based multiplayer games.  \r\n// If this is 0, players will not be able to see your server via the in-game browser nor gamespy arcade.\r\nset sv_gamespy 1\r\n\r\n// Flood Protect\r\n// Limits amount of text a player can type\r\nset sv_floodprotect 1\r\n\r\n// Forces players to only be able to spectate behind their own team. \r\nset g_forceteamspectate 1\r\n\r\n//  dmflags -- flags that can be set in the dmflags variable.\r\n//  DF_NO_HEALTH		(1 << 0)\r\n//  DF_NO_POWERUPS	(1 << 1)\r\n//  DF_WEAPONS_STAY	(1 << 2)\r\n//  DF_NO_FALLING		(1 << 3)\r\n//  DF_INSTANT_ITEMS	(1 << 4)\r\n//  DF_SAME_LEVEL		(1 << 5)\r\n//  DF_NO_ARMOR		(1 << 11)\r\n//  DF_INFINITE_AMMO	(1 << 14)\r\n//  DF_NO_FOOTSTEPS	(1 << 17)\r\n//  DF_ALLOW_LEAN		(1 << 18)\r\n//  DF_OLD_SNIPERRIFLE	(1 << 19)\r\n//  DF_GERMAN_SHOTGUN	(1 << 20)\r\nset dmflags 0\r\n\r\n// Invulnerable Time\r\n// Amount of time (in seconds) a player is invulnerable for after spawning (default=3 seconds)\r\nset sv_invulnerabletime 3\r\n\r\n// Team Kill Warning\r\n// Amount of team kills before the player is warned\r\nset g_teamkillwarn 3\r\n\r\n// Team Kill Kick\r\n// Amount of team kills before the player is kicked off the server.\r\nset g_teamkillkick 5\r\n\r\n// Team Switch Delay\r\n// Frequency at which you can switch teams\r\nset g_teamswitchdelay 15\r\n\r\n\r\n// Allow Join Time\r\nset g_allowjointime 30\r\n\r\n// Keywords\r\n// Keywords which allow browsers to filter servers.\r\nset sv_keywords \"MOHAA Server\"\r\n\r\n// Net Port\r\nset net_ip $[Service.IpAddress]\r\nset net_port $[Service.GamePort]\r\n\r\n// Minimum Ping to allow clients to join with.  0 means anyone\r\nset sv_minping 0\r\n\r\n// Maximum Ping to allow clients to join with.  0 means anyone\r\nset sv_maxping 200\r\n\r\n// Maximum Rate\r\nset sv_maxrate 0\r\n// Default to no-sprint and original Allied Assault runspeed\r\nsv_sprinton 0\r\nsv_runspeed 250	// spearhead runspeed is 287\r\n\r\n// Map Rotation List\r\nsv_maplist \"obj/obj_team1 obj/obj_team2 obj/obj_team3 obj/obj_team4 \"\r\n\r\n// Map\r\n// Starting map on the rotation. Kicks the server into gear\r\nmap \"obj/obj_team1\"\r\n', NULL, 1, 0, '<?xml version=\"1.0\" encoding=\"utf-16\" standalone=\"yes\"?>\r\n<values>\r\n  <add key=\"__TCA:CREATED_BY\" value=\"3\" type=\"System.Int32,mscorlib\" />\r\n  <add key=\"__TCA:CREATED_ON\" value=\"2011-07-27 15:53:57\" type=\"System.DateTime,mscorlib\" />\r\n</values>', NULL, 1),
(34, 1, 0, 'mainta\\server.cfg', 'Server Configuration File', NULL, NULL, NULL, '//-----------------------------------------------------------------------\r\n//\r\n//	MOHAA Server Configuration File\r\n//\r\n//-----------------------------------------------------------------------\r\n\r\n// Server Name\r\n// Name that will appear on the server browser\r\nsv_hostname \"$[HostName]\"\r\n\r\n// Game Type\r\n// 1 Free for All\r\n// 2 Team Based\r\n// 3 Round Based\r\n// 4 Objective\r\n// 5 Tug Of War\r\nset g_gametype 1\r\n\r\n// Health Drop\r\n// When enabled players will drop a health pack when killed\r\nset g_healthdrop 1\r\n\r\n// Realism\r\n// Toggles reaslistic mode\r\nset g_realismmode 0\r\n\r\n// Fast Run Speed\r\n// When checked, the speed at which players can run through levels is increased. \r\n// Leaving it unchecked defaults to Medal of Honor Allied Assault\'s original run speed\r\nset sv_dmspeedmult 1.100000\r\n\r\n// Team Damage\r\n// Toggles friendly fire\r\nset g_teamdamage 0\r\n\r\n// Heal Rate\r\n// Determines how fast players heal when they use a health pickup\r\nset g_healrate 10\r\n\r\n\r\n// Time Limit\r\n// This sets the elapsed time at which the game ends, the winner being the player with the highest score at that time.\r\ntimelimit 0\r\n\r\n// Frag Limit\r\n// The score at which the player or team will win the current map.\r\nfraglimit 0\r\n\r\n// Map Rotation Time\r\n// This sets the maximum amount of time in minutes players will spend in a particular map.\r\nmaprotationtime 0\r\n\r\n// Team Spawn Delay\r\n// This sets a delay in seconds between spawns. This is useful if you would like the \r\n// player spawned in batches, rather than one at a time\r\nset sv_team_spawn_interval 15\r\n\r\n// Round Reset Time\r\n// This sets how long each round will take, in minutes. This can be smaller than the map rotation time, but not larger.\r\nroundlimit 0\r\n\r\n// Inactive Spectate\r\n// This sets the time in seconds after which an inactive player becomes a spectator. To save server bandwidth, 60 seconds is default.\r\nset g_inactivespectate 60\r\n\r\n// Inactive Kick\r\n// This sets the time in seconds after which an inactive player is kicked out of the game. 900 (15 minutes) seconds is default.\r\nset g_inactivekick 900\r\n\r\n// Use Gamespy\r\n// To use GameSpy gaming service for internet-based multiplayer games.  \r\n// If this is 0, players will not be able to see your server via the in-game browser nor gamespy arcade.\r\nset sv_gamespy 1\r\n\r\n// Pure\r\n// Verifies that the files on the client are the same as those on the server.\r\nset sv_pure 1\r\n\r\n\r\n// Allow Vote\r\n// Enables players to call a vote.\r\nset g_allowvote 1\r\n\r\n// Flood Protect\r\n// Limits amount of text a player can type\r\nset sv_floodprotect 1\r\n\r\n// Forces players to only be able to spectate behind their own team. \r\nset g_forceteamspectate 1\r\n\r\n//  dmflags -- flags that can be set in the dmflags variable.\r\n//  DF_NO_HEALTH		(1 << 0)\r\n//  DF_NO_POWERUPS		(1 << 1)\r\n//  DF_WEAPONS_STAY		(1 << 2)\r\n//  DF_NO_FALLING		(1 << 3)\r\n//  DF_INSTANT_ITEMS	(1 << 4)\r\n//  DF_SAME_LEVEL		(1 << 5)\r\n//  DF_NO_ARMOR			(1 << 11)\r\n//  DF_INFINITE_AMMO	(1 << 14)\r\n//  DF_NO_FOOTSTEPS		(1 << 17)\r\n//  DF_ALLOW_LEAN		(1 << 18)\r\n//  DF_OLD_SNIPERRIFLE	(1 << 19)\r\n//  DF_GERMAN_SHOTGUN	(1 << 20)\r\nset dmflags 0\r\n\r\n// Invulnerable Time\r\n// Amount of time (in seconds) a player is invulnerable for after spawning (default=3 seconds)\r\nset sv_invulnerabletime 3\r\n\r\n// Team Kill Warning\r\n// Amount of team kills before the player is warned\r\nset g_teamkillwarn 3\r\n\r\n// Team Kill Kick\r\n// Amount of team kills before the player is kicked off the server.\r\nset g_teamkillkick 5\r\n\r\n// Team Switch Delay\r\n// Frequency at which you can switch teams\r\nset g_teamswitchdelay 15\r\n\r\n\r\n// Allow Join Time\r\nset g_allowjointime 30\r\n\r\n// Private Clients\r\n// Clients that require a password to join\r\nset sv_privateclients 0\r\n\r\n// Password\r\n// Allows you to set a password for the server\r\nset sv_privatepassword \"$[PrivatePassword]\"\r\n\r\n// Keywords\r\n// Keywords which allow browsers to filter servers.\r\nset sv_keywords \"\"\r\n\r\n\r\n// Minimum Ping to allow clients to join with.  0 means anyone\r\nset sv_minping 0\r\n\r\n// Maximum Ping to allow clients to join with.  0 means anyone\r\nset sv_maxping 0\r\n\r\n// Maximum Rate\r\nset sv_maxrate 0\r\n\r\n// Map\r\n// Starting map on the rotation.\r\nmap \"dm/MP_Holland_DM\"\r\n\r\n// Map Rotation List\r\nsv_maplist \"dm/MP_Bahnhof_DM dm/MP_Verschneit_DM dm/MP_Brest_DM dm/MP_Unterseite_DM dm/MP_Stadt_DM dm/MP_Gewitter_DM dm/MP_Holland_DM dm/MP_Bazaar_DM \"\r\n\r\n\r\n// Default to no-sprint and original Allied Assault runspeed\r\nsv_sprinton 0\r\nsv_runspeed 250	// spearhead runspeed is 287\r\n', NULL, 1, 0, '<?xml version=\"1.0\" encoding=\"utf-16\" standalone=\"yes\"?>\r\n<values>\r\n  <add key=\"__TCA:CREATED_BY\" value=\"3\" type=\"System.Int32,mscorlib\" />\r\n  <add key=\"__TCA:CREATED_ON\" value=\"2011-07-27 15:54:08\" type=\"System.DateTime,mscorlib\" />\r\n</values>', NULL, 1),
