(142, 1, 0, 'server.cfg', 'Server configuration', NULL, NULL, 0, '//ArmA 2 Server Configuration File\n\n\n// comments are written with \"//\" in front of them.\n// server.cfg\n\n// password to protect admin access\npasswordAdmin = \"$[RConPassword]\"; \n\n// password required to connect to server\npassword = \"$[PrivatePassword]\"; \n\n// Server display name\nhostname=\"$[HostName]\";\n\n// Number of slots  Exceeding Max slots purchased will disable the server automatically!\nmaxPlayers =$[Service.Slots];\n\n// Welcome message, two lines \",\" means \'new line\'\nmotd[]= {\"Powered by ExecuteGaming.com\",}; \n\n// if motd has multiple lines, how fast to show these in succession (default is 5 seconds).\nmotdInterval=1; \n\n// when one third agrees, this is enough to confirm a vote\nvoteThreshold=0.33; \n\n// private server - no reporting (\"armedass.master.gamespy.com\" to report to the master server)\nreportingIP=\"arma2pc.master.gamespy.com\"; \n\n// start voting for missions when 3 players connect\nvoteMissionPlayers=1; \n\n//list of files to check for identity\ncheckfiles[]={\"HWTL\\dta\\data3d.pbo\",\"dta\\data3d.pbo\"}; \n\n// do not allow duplicate id\nkickduplicate=1;  \n\n// require equal mod\nequalModRequired=1; \n\n//Disables voice over net\ndisableVoN=0; \n\n// Enables or disables the signature verification for addons. Default = 0\nverifySignatures=0; \n\n// Enables or disables the persistent battlefield. Default 0.\npersistent=0; \n\n// Map rotation\nclass Missions\n{\nclass Mission_01\n    {\n    template = \"coop_destroy_base.utes\";\n    difficulty=\"regular\";\n};\n};\n', 0, 1, 0, '<?xml version=\"1.0\" encoding=\"utf-16\" standalone=\"yes\"?>\r\n<values>\r\n  <add key=\"__TCA:CREATED_BY\" value=\"3\" type=\"System.Int32,mscorlib\" />\r\n  <add key=\"__TCA:CREATED_ON\" value=\"2011-08-07 01:57:21\" type=\"System.DateTime,mscorlib\" />\r\n  <add key=\"__TCA:NOTES\" value=\"\" type=\"System.String,mscorlib\" />\r\n  <add key=\"__TCA:MODIFIED_BY\" value