INSERT INTO `game_config_files` (`game_id`, `file_id`, `mod_id`, `relative_path`, `description`, `enable_text_editor_reseller`, `enable_text_editor_user`, `view_order`, `template`, `enable_config_editor`, `read_values`, `hide_path`, `app_data`, `hide_read_failure`, `update_automatically`) VALUES
(127, 1, 0, 'serverconfig.xml', 'Main configuration file', NULL, NULL, 0, '<?xml version=\"1.0\"?>\n<ServerSettings>\n	<!-- GENERAL SERVER SETTINGS -->\n\n	<!-- Server representation -->\n	<property name=\"ServerName\"						value=\"$[HostName]\"/>		<!-- Whatever you want the name of the server to be. -->\n	<property name=\"ServerDescription\"				value=\"A 7 Days to Die server\"/>	<!-- Whatever you want the server description to be, will be shown in the server browser. -->\n	<property name=\"ServerWebsiteURL\"				value=\"\"/>					<!-- Website URL for the server, will be shown in the serverbrowser as a clickable link -->\n	<property name=\"ServerPassword\"					value=\"$[PrivatePassword]\"/>					<!-- Password to gain entry to the server -->\n	<property name=\"ServerLoginConfirmationText\"	value=\"\" />					<!-- If set the user will see the message during joining the server and has to confirm it before continuing. For more complex changes to this window you can change the \"serverjoinrulesdialog\" window in XUi -->\n\n	<!-- Networking -->\n	<property name=\"ServerPort\"						value=\"$[Service.GamePort]\"/>				<!-- Port you want the server to listen on. -->\n	<property name=\"ServerVisibility\"				value=\"2\"/>					<!-- Visibility of this server: 2 = public, 1 = only shown to friends, 0 = not listed. As you are never friend of a dedicated server setting this to \"1\" will only work when the first player connects manually by IP. -->\n	<property name=\"ServerDisabledNetworkProtocols\"	value=\"SteamNetworking\"/>	<!-- Networking protocols that should not be used. Separated by comma. Possible values: LiteNetLib, SteamNetworking. Dedicated servers should disable SteamNetworking if there is no NAT router in between your users and the server or when port-forwarding is set up correctly -->\n	<property name=\"ServerMaxWorldTransferSpeedKiBs\" value=\"512\"/>				<!-- Maximum (!) speed in kiB/s the world is transferred at to a client on first connect if it does not have the world yet. Maximum is about 1300 kiB/s, even if you set a higher value. -->\n\n	<!-- Slots -->\n	<property name=\"ServerMaxPlayerCount\"			value=\"$[Service.Slots]\"/>					<!-- Maximum Concurrent Players -->\n	<property name=\"ServerReservedSlots\"			value=\"0\"/>					<!-- Out of the MaxPlayerCount this many slots can only be used by players with a specific permission level -->\n	<property name=\"ServerReservedSlotsPermission\"	value=\"100\"/>				<!-- Required permission level to use reserved slots above -->\n	<property name=\"ServerAdminSlots\"				value=\"0\"/>					<!-- This many admins can still join even if the server has reached MaxPlayerCount -->\n	<property name=\"ServerAdminSlotsPermission\"		value=\"0\"/>					<!-- Required permission level to use the admin slots above -->\n\n	<!-- Admin interfaces -->\n	<property name=\"ControlPanelEnabled\"			value=\"false\"/>				<!-- Enable/Disable the web control panel -->\n	<property name=\"ControlPanelPort\"				value=\"$[Service.CustomPort1]\"/>				<!-- Port of the control panel webpage -->\n	<property name=\"ControlPanelPassword\"			value=\"CHANGEME\"/>			<!-- Password to gain entry to the control panel -->\n\n	<property name=\"TelnetEnabled\"					value=\"true\"/>				<!-- Enable/Disable the telnet -->\n	<property name=\"TelnetPort\"						value=\"$[Service.RConPort]\"/>				<!-- Port of the telnet server -->\n	<property name=\"TelnetPassword\"					value=\"$[RConPassword]\"/>					<!-- Password to gain entry to telnet interface. If no password is set the server will only listen on the local loopback interface -->\n	<property name=\"TelnetFailedLoginLimit\"			value=\"10\"/>				<!-- After this many wrong passwords from a single remote client the client will be blocked from connecting to the Telnet interface -->\n	<property name=\"TelnetFailedLoginsBlocktime\"	value=\"10\"/>				<!-- How long will the block persist (in seconds) -->\n\n	<property name=\"TerminalWindowEnabled\"			value=\"true\"/>				<!-- Show a terminal window for log output / command input (Windows only) -->\n\n	<!-- Folder and file locations -->\n	<property name=\"AdminFileName\"					value=\"serveradmin.xml\"/>	<!-- Server admin file name. Path relative to the SaveGameFolder -->\n	<!-- <property name=\"UserDataFolder\"				value=\"absolute path\" /> -->	<!-- Use this to override where the server stores all generated data, including RWG generated worlds. Do not forget to uncomment the entry! -->\n	<!-- <property name=\"SaveGameFolder\"				value=\"absolute path\" /> -->	<!-- Use this to only override the save game path. Do not forget to uncomment the entry! -->\n\n	<!-- Other technical settings -->\n	<property name=\"EACEnabled\"						value=\"true\"/>				<!-- Enables/Disables EasyAntiCheat -->\n	<property name=\"HideCommandExecutionLog\"		value=\"0\"/>					<!-- Hide logging of command execution. 0 = show everything, 1 = hide only from Telnet/ControlPanel, 2 = also hide from remote game clients, 3 = hide everything -->\n	<property name=\"MaxUncoveredMapChunksPerPlayer\"	value=\"131072\"/>			<!-- Override how many chunks can be uncovered on the ingame map by each player. Resulting max map file size limit per player is (x * 512 Bytes), uncovered area is (x * 256 m). Default 131072 means max 32 km can be uncovered at any time -->\n	<property name=\"PersistentPlayerProfiles\"		value=\"false\" />			<!-- If disabled a player can join with any selected profile. If true they will join with the last profile they joined with -->\n\n\n\n	<!-- GAMEPLAY -->\n	\n	<!-- World -->\n	<property name=\"GameWorld\"						value=\"Navezgane\"/>			<!-- \"RWG\" (see WorldGenSeed and WorldGenSize options below) or any already existing world name in the Worlds folder (currently shipping with e.g. \"Navezgane\", \"PREGEN01\", ...) -->\n	<property name=\"WorldGenSeed\"					value=\"asdf\"/>				<!-- If RWG this is the seed for the generation of the new world. If a world with the resulting name already exists it will simply load it -->\n	<property name=\"WorldGenSize\"					value=\"4096\"/>				<!-- If RWG this controls the width and height of the created world. It is also used in combination with WorldGenSeed to create the internal RWG seed thus also creating a unique map name even if using the same WorldGenSeed. Has to be between 2048 and 16384, though large map sizes will take long to generate / download / load -->\n	<property name=\"GameName\"						value=\"My Game\"/>			<!-- Whatever you want the game name to be. This affects the save game name as well as the seed used when placing decoration (trees etc) in the world. It does not control the generic layout of the world if creating an RWG world -->\n	<property name=\"GameMode\"						value=\"GameModeSurvival\"/>	<!-- GameModeSurvival -->\n\n	<!-- Difficulty -->\n	<property name=\"GameDifficulty\"					value=\"2\"/>					<!-- 0 - 5, 0=easiest, 5=hardest -->\n	<property name=\"BlockDamagePlayer\"				value=\"100\" />				<!-- How much damage do players to blocks (percentage in whole numbers) -->\n	<property name=\"BlockDamageAI\"					value=\"100\" />				<!-- How much damage do AIs to blocks (percentage in whole numbers) -->\n	<property name=\"BlockDamageAIBM\"				value=\"100\" />				<!-- How much damage do AIs during blood moons to blocks (percentage in whole numbers) -->\n	<property name=\"XPMultiplier\"					value=\"100\" />				<!-- XP gain multiplier (percentage in whole numbers) -->\n	<property name=\"PlayerSafeZoneLevel\"			value=\"5\" />				<!-- If a player is less or equal this level he will create a safe zone (no enemies) when spawned -->\n	<property name=\"PlayerSafeZoneHours\"			value=\"5\" />				<!-- Hours in world time this safe zone exists -->\n\n	<!--  -->\n	<property name=\"BuildCreate\"					value=\"false\" />			<!-- cheat mode on/off -->\n	<property name=\"DayNightLength\"					value=\"60\" />				<!-- real time minutes per in game day: 60 minutes -->\n	<property name=\"DayLightLength\"					value=\"18\" />				<!-- in game hours the sun shines per day: 18 hours day light per in game day -->\n	<property name=\"DropOnDeath\"					value=\"1\" />				<!-- 0 = nothing, 1 = everything, 2 = toolbelt only, 3 = backpack only, 4 = delete all -->\n	<property name=\"DropOnQuit\"						value=\"0\" />				<!-- 0 = nothing, 1 = everything, 2 = toolbelt only, 3 = backpack only -->\n	<property name=\"BedrollDeadZoneSize\"			value=\"15\" />				<!-- Size (box \"radius\", so a box with 2 times the given value for each side\'s length) of bedroll deadzone, no zombies will spawn inside this area, and any cleared sleeper volumes that touch a bedroll deadzone will not spawn after they\'ve been cleared. -->\n	<property name=\"BedrollExpiryTime\"				value=\"45\" />				<!-- Number of days a bedroll stays active after owner was last online -->\n\n	<!-- Performance related -->\n	<property name=\"MaxSpawnedZombies\"				value=\"64\" />				<!-- This setting covers the entire map. There can only be this many zombies on the entire map at one time. Changing this setting has a huge impact on performance. -->\n	<property name=\"MaxSpawnedAnimals\"				value=\"50\" />				<!-- If your server has a large number of players you can increase this limit to add more wildlife. Animals don\'t consume as much CPU as zombies. NOTE: That this doesn\'t cause more animals to spawn arbitrarily: The biome spawning system only spawns a certain number of animals in a given area, but if you have lots of players that are all spread out then you may be hitting the limit and can increase it. -->\n	<property name=\"ServerMaxAllowedViewDistance\"	value=\"12\" />				<!-- Max viewdistance a client may request (6 - 12). High impact on memory usage and performance. -->\n\n	<!-- Zombie settings -->\n	<property name=\"EnemySpawnMode\"					value=\"true\" />				<!-- Enable/Disable enemy spawning -->\n	<property name=\"EnemyDifficulty\"				value=\"0\" />				<!-- 0 = Normal, 1 = Feral -->\n	<property name=\"ZombieMove\"						value=\"0\" />				<!-- 0-4 (walk, jog, run, sprint, nightmare) -->\n	<property name=\"ZombieMoveNight\"				value=\"3\" />				<!-- 0-4 (walk, jog, run, sprint, nightmare) -->\n	<property name=\"ZombieFeralMove\"				value=\"3\" />				<!-- 0-4 (walk, jog, run, sprint, nightmare) -->\n	<property name=\"ZombieBMMove\"					value=\"3\" />				<!-- 0-4 (walk, jog, run, sprint, nightmare) -->\n	<property name=\"BloodMoonFrequency\"				value=\"7\" />				<!-- What frequency (in days) should a blood moon take place. Set to \"0\" for no blood moons -->\n	<property name=\"BloodMoonRange\"					value=\"0\" />				<!-- How many days can the actual blood moon day randomly deviate from the above setting. Setting this to 0 makes blood moons happen exactly each Nth day as specified in BloodMoonFrequency -->\n	<property name=\"BloodMoonWarning\"				value=\"8\" />				<!-- The Hour number that the red day number begins on a blood moon day. Setting this to -1 makes the red never show.  -->\n	<property name=\"BloodMoonEnemyCount\"			value=\"8\" />				<!-- This is the number of zombies that can be alive (spawned at the same time) at any time PER PLAYER during a blood moon horde, however, MaxSpawnedZombies overrides this number in multiplayer games. Also note that your game stage sets the max number of zombies PER PARTY. Low game stage values can result in lower number of zombies than the BloodMoonEnemyCount setting. Changing this setting has a huge impact on performance. -->\n\n	<!-- Loot -->\n	<property name=\"LootAbundance\"					value=\"100\" />				<!-- percentage in whole numbers -->\n	<property name=\"LootRespawnDays\"				value=\"30\" />				<!-- days in whole numbers -->\n	<property name=\"AirDropFrequency\"				value=\"72\"/>				<!-- How often airdrop occur in game-hours, 0 == never -->\n	<property name=\"AirDropMarker\"					value=\"false\"/>				<!-- Sets if a marker is added to map/compass for air drops. -->\n\n	<!-- Multiplayer -->\n	<property name=\"PartySharedKillRange\"			value=\"100\"/>				<!-- The distance you must be within to receive party shared kill xp and quest party kill objective credit. -->\n	<property name=\"PlayerKillingMode\"				value=\"3\" />				<!-- Player Killing Settings (0 = No Killing, 1 = Kill Allies Only, 2 = Kill Strangers Only, 3 = Kill Everyone) -->\n\n	<!-- Land claim options -->\n	<property name=\"LandClaimCount\"					value=\"1\"/>					<!-- Maximum allowed land claims per player. -->\n	<property name=\"LandClaimSize\"					value=\"41\"/>				<!-- Size in blocks that is protected by a keystone -->\n	<property name=\"LandClaimDeadZone\"				value=\"30\"/>				<!-- Keystones must be this many blocks apart (unless you are friends with the other player) -->\n	<property name=\"LandClaimExpiryTime\"			value=\"7\"/>					<!-- The number of days a player can be offline before their claims expire and are no longer protected -->\n	<property name=\"LandClaimDecayMode\"				value=\"0\"/>					<!-- Controls how offline players land claims decay. 0=Slow (Linear) , 1=Fast (Exponential), 2=None (Full protection until claim is expired). -->\n	<property name=\"LandClaimOnlineDurabilityModifier\"	value=\"4\"/>				<!-- How much protected claim area block hardness is increased when a player is online. 0 means infinite (no damage will ever be taken). Default is 4x -->\n	<property name=\"LandClaimOfflineDurabilityModifier\"	value=\"4\"/>				<!-- How much protected claim area block hardness is increased when a player is offline. 0 means infinite (no damage will ever be taken). Default is 4x -->\n	<property name=\"LandClaimOfflineDelay\"			value=\"0\"/>					<!-- The number of minutes after a player logs out that the land claim area hardness transitions from online to offline. Default is 0 -->\n\n\n	<!-- There are several game settings that you cannot change when starting a new game.\n	You can use console commands to change at least some of them ingame.\n	setgamepref BedrollDeadZoneSize 30 -->\n</ServerSettings>', 0, 0, 0, '<?xml version=\"1.0\" encoding=\"utf-16\" standalone=\"yes\"?>\r\n<values>\r\n  <add key=\"__TCA:CREATED_BY\" value=\"3\" type=\"System.Int32,mscorlib\" />\r\n  <add key=\"__TCA:CREATED_ON\" value=\"2018-04-19 18:47:48\" type=\"System.DateTime,mscorlib\" />\r\n  <add key=\"__TCA:NOTES\" value=\"\" type=\"System.String,mscorlib\" />\r\n  <add key=\"__TCA:Encoding\" value=\"0\" type=\"System.Int32,mscorlib\" />\r\n  <add key=\"__TCA:MODIFIED_BY\" value=\"3\" type=\"System.Int32,mscorlib\" />\r\n  <add key=\"__TCA:MODIFIED_ON\" value=\"2020-07-11 21:40:46\" type=\"System.DateTime,mscorlib\" />\r\n</values>', 0, 1),
(128, 1, 0, 'serverconfig.xml', 'Main configuration file', NULL, NULL, 0, '<?xml version=\"1.0\"?>\n<ServerSettings>\n	<!-- GENERAL SERVER SETTINGS -->\n\n	<!-- Server representation -->\n	<property name=\"ServerName\"						value=\"$[HostName]\"/>		<!-- Whatever you want the name of the server to be. -->\n	<property name=\"ServerDescription\"				value=\"A 7 Days to Die server\"/>	<!-- Whatever you want the server description to be, will be shown in the server browser. -->\n	<property name=\"ServerWebsiteURL\"				value=\"\"/>					<!-- Website URL for the server, will be shown in the serverbrowser as a clickable link -->\n	<property name=\"ServerPassword\"					value=\"$[PrivatePassword]\"/>					<!-- Password to gain entry to the server -->\n	<property name=\"ServerLoginConfirmationText\"	value=\"\" />					<!-- If set the user will see the message during joining the server and has to confirm it before continuing. For more complex changes to this window you can change the \"serverjoinrulesdialog\" window in XUi -->\n\n	<!-- Networking -->\n	<property name=\"ServerPort\"						value=\"$[Service.GamePort]\"/>				<!-- Port you want the server to listen on. -->\n	<property name=\"ServerVisibility\"				value=\"2\"/>					<!-- Visibility of this server: 2 = public, 1 = only shown to friends, 0 = not listed. As you are never friend of a dedicated server setting this to \"1\" will only work when the first player connects manually by IP. -->\n	<property name=\"ServerDisabledNetworkProtocols\"	value=\"SteamNetworking\"/>	<!-- Networking protocols that should not be used. Separated by comma. Possible values: LiteNetLib, SteamNetworking. Dedicated servers should disable SteamNetworking if there is no NAT router in between your users and the server or when port-forwarding is set up correctly -->\n	<property name=\"ServerMaxWorldTransferSpeedKiBs\" value=\"512\"/>				<!-- Maximum (!) speed in kiB/s the world is transferred at to a client on first connect if it does not have the world yet. Maximum is about 1300 kiB/s, even if you set a higher value. -->\n\n	<!-- Slots -->\n	<property name=\"ServerMaxPlayerCount\"			value=\"$[Service.Slots]\"/>					<!-- Maximum Concurrent Players -->\n	<property name=\"ServerReservedSlots\"			value=\"0\"/>					<!-- Out of the MaxPlayerCount this many slots can only be used by players with a specific permission level -->\n	<property name=\"ServerReservedSlotsPermission\"	value=\"100\"/>				<!-- Required permission level to use reserved slots above -->\n	<property name=\"ServerAdminSlots\"				value=\"0\"/>					<!-- This many admins can still join even if the server has reached MaxPlayerCount -->\n	<property name=\"ServerAdminSlotsPermission\"		value=\"0\"/>					<!-- Required permission level to use the admin slots above -->\n\n	<!-- Admin interfaces -->\n	<property name=\"ControlPanelEnabled\"			value=\"false\"/>				<!-- Enable/Disable the web control panel -->\n	<property name=\"ControlPanelPort\"				value=\"$[Service.CustomPort1]\"/>				<!-- Port of the control panel webpage -->\n	<property name=\"ControlPanelPassword\"			value=\"CHANGEME\"/>			<!-- Password to gain entry to the control panel -->\n\n	<property name=\"TelnetEnabled\"					value=\"true\"/>				<!-- Enable/Disable the telnet -->\n	<property name=\"TelnetPort\"						value=\"$[Service.RConPort]\"/>				<!-- Port of the telnet server -->\n	<property name=\"TelnetPassword\"					value=\"$[RConPassword]\"/>					<!-- Password to gain entry to telnet interface. If no password is set the server will only listen on the local loopback interface -->\n	<property name=\"TelnetFailedLoginLimit\"			value=\"10\"/>				<!-- After this many wrong passwords from a single remote client the client will be blocked from connecting to the Telnet interface -->\n	<property name=\"TelnetFailedLoginsBlocktime\"	value=\"10\"/>				<!-- How long will the block persist (in seconds) -->\n\n	<property name=\"TerminalWindowEnabled\"			value=\"true\"/>				<!-- Show a terminal window for log output / command input (Windows only) -->\n\n	<!-- Folder and file locations -->\n	<property name=\"AdminFileName\"					value=\"serveradmin.xml\"/>	<!-- Server admin file name. Path relative to the SaveGameFolder -->\n	<!-- <property name=\"UserDataFolder\"				value=\"absolute path\" /> -->	<!-- Use this to override where the server stores all generated data, including RWG generated worlds. Do not forget to uncomment the entry! -->\n	<!-- <property name=\"SaveGameFolder\"				value=\"absolute path\" /> -->	<!-- Use this to only override the save game path. Do not forget to uncomment the entry! -->\n\n	<!-- Other technical settings -->\n	<property name=\"EACEnabled\"						value=\"true\"/>				<!-- Enables/Disables EasyAntiCheat -->\n	<property name=\"HideCommandExecutionLog\"		value=\"0\"/>					<!-- Hide logging of command execution. 0 = show everything, 1 = hide only from Telnet/ControlPanel, 2 = also hide from remote game clients, 3 = hide everything -->\n	<property name=\"MaxUncoveredMapChunksPerPlayer\"	value=\"131072\"/>			<!-- Override how many chunks can be uncovered on the ingame map by each player. Resulting max map file size limit per player is (x * 512 Bytes), uncovered area is (x * 256 m). Default 131072 means max 32 km can be uncovered at any time -->\n	<property name=\"PersistentPlayerProfiles\"		value=\"false\" />			<!-- If disabled a player can join with any selected profile. If true they will join with the last profile they joined with -->\n\n\n\n	<!-- GAMEPLAY -->\n	\n	<!-- World -->\n	<property name=\"GameWorld\"						value=\"Navezgane\"/>			<!-- \"RWG\" (see WorldGenSeed and WorldGenSize options below) or any already existing world name in the Worlds folder (currently shipping with e.g. \"Navezgane\", \"PREGEN01\", ...) -->\n	<property name=\"WorldGenSeed\"					value=\"asdf\"/>				<!-- If RWG this is the seed for the generation of the new world. If a world with the resulting name already exists it will simply load it -->\n	<property name=\"WorldGenSize\"					value=\"4096\"/>				<!-- If RWG this controls the width and height of the created world. It is also used in combination with WorldGenSeed to create the internal RWG seed thus also creating a unique map name even if using the same WorldGenSeed. Has to be between 2048 and 16384, though large map sizes will take long to generate / download / load -->\n	<property name=\"GameName\"						value=\"My Game\"/>			<!-- Whatever you want the game name to be. This affects the save game name as well as the seed used when placing decoration (trees etc) in the world. It does not control the generic layout of the world if creating an RWG world -->\n	<property name=\"GameMode\"						value=\"GameModeSurvival\"/>	<!-- GameModeSurvival -->\n\n	<!-- Difficulty -->\n	<property name=\"GameDifficulty\"					value=\"2\"/>					<!-- 0 - 5, 0=easiest, 5=hardest -->\n	<property name=\"BlockDamagePlayer\"				value=\"100\" />				<!-- How much damage do players to blocks (percentage in whole numbers) -->\n	<property name=\"BlockDamageAI\"					value=\"100\" />				<!-- How much damage do AIs to blocks (percentage in whole numbers) -->\n	<property name=\"BlockDamageAIBM\"				value=\"100\" />				<!-- How much damage do AIs during blood moons to blocks (percentage in whole numbers) -->\n	<property name=\"XPMultiplier\"					value=\"100\" />				<!-- XP gain multiplier (percentage in whole numbers) -->\n	<property name=\"PlayerSafeZoneLevel\"			value=\"5\" />				<!-- If a player is less or equal this level he will create a safe zone (no enemies) when spawned -->\n	<property name=\"PlayerSafeZoneHours\"			value=\"5\" />				<!-- Hours in world time this safe zone exists -->\n\n	<!--  -->\n	<property name=\"BuildCreate\"					value=\"false\" />			<!-- cheat mode on/off -->\n	<property name=\"DayNightLength\"					value=\"60\" />				<!-- real time minutes per in game day: 60 minutes -->\n	<property name=\"DayLightLength\"					value=\"18\" />				<!-- in game hours the sun shines per day: 18 hours day light per in game day -->\n	<property name=\"DropOnDeath\"					value=\"1\" />				<!-- 0 = nothing, 1 = everything, 2 = toolbelt only, 3 = backpack only, 4 = delete all -->\n	<property name=\"DropOnQuit\"						value=\"0\" />				<!-- 0 = nothing, 1 = everything, 2 = toolbelt only, 3 = backpack only -->\n	<property name=\"BedrollDeadZoneSize\"			value=\"15\" />				<!-- Size (box \"radius\", so a box with 2 times the given value for each side\'s length) of bedroll deadzone, no zombies will spawn inside this area, and any cleared sleeper volumes that touch a bedroll deadzone will not spawn after they\'ve been cleared. -->\n	<property name=\"BedrollExpiryTime\"				value=\"45\" />				<!-- Number of days a bedroll stays active after owner was last online -->\n\n	<!-- Performance related -->\n	<property name=\"MaxSpawnedZombies\"				value=\"64\" />				<!-- This setting covers the entire map. There can only be this many zombies on the entire map at one time. Changing this setting has a huge impact on performance. -->\n	<property name=\"MaxSpawnedAnimals\"				value=\"50\" />				<!-- If your server has a large number of players you can increase this limit to add more wildlife. Animals don\'t consume as much CPU as zombies. NOTE: That this doesn\'t cause more animals to spawn arbitrarily: The biome spawning system only spawns a certain number of animals in a given area, but if you have lots of players that are all spread out then you may be hitting the limit and can increase it. -->\n	<property name=\"ServerMaxAllowedViewDistance\"	value=\"12\" />				<!-- Max viewdistance a client may request (6 - 12). High impact on memory usage and performance. -->\n\n	<!-- Zombie settings -->\n	<property name=\"EnemySpawnMode\"					value=\"true\" />				<!-- Enable/Disable enemy spawning -->\n	<property name=\"EnemyDifficulty\"				value=\"0\" />				<!-- 0 = Normal, 1 = Feral -->\n	<property name=\"ZombieMove\"						value=\"0\" />				<!-- 0-4 (walk, jog, run, sprint, nightmare) -->\n	<property name=\"ZombieMoveNight\"				value=\"3\" />				<!-- 0-4 (walk, jog, run, sprint, nightmare) -->\n	<property name=\"ZombieFeralMove\"				value=\"3\" />				<!-- 0-4 (walk, jog, run, sprint, nightmare) -->\n	<property name=\"ZombieBMMove\"					value=\"3\" />				<!-- 0-4 (walk, jog, run, sprint, nightmare) -->\n	<property name=\"BloodMoonFrequency\"				value=\"7\" />				<!-- What frequency (in days) should a blood moon take place. Set to \"0\" for no blood moons -->\n	<property name=\"BloodMoonRange\"					value=\"0\" />				<!-- How many days can the actual blood moon day randomly deviate from the above setting. Setting this to 0 makes blood moons happen exactly each Nth day as specified in BloodMoonFrequency -->\n	<property name=\"BloodMoonWarning\"				value=\"8\" />				<!-- The Hour number that the red day number begins on a blood moon day. Setting this to -1 makes the red never show.  -->\n	<property name=\"BloodMoonEnemyCount\"			value=\"8\" />				<!-- This is the number of zombies that can be alive (spawned at the same time) at any time PER PLAYER during a blood moon horde, however, MaxSpawnedZombies overrides this number in multiplayer games. Also note that your game stage sets the max number of zombies PER PARTY. Low game stage values can result in lower number of zombies than the BloodMoonEnemyCount setting. Changing this setting has a huge impact on performance. -->\n\n	<!-- Loot -->\n	<property name=\"LootAbundance\"					value=\"100\" />				<!-- percentage in whole numbers -->\n	<property name=\"LootRespawnDays\"				value=\"30\" />				<!-- days in whole numbers -->\n	<property name=\"AirDropFrequency\"				value=\"72\"/>				<!-- How often airdrop occur in game-hours, 0 == never -->\n	<property name=\"AirDropMarker\"					value=\"false\"/>				<!-- Sets if a marker is added to map/compass for air drops. -->\n\n	<!-- Multiplayer -->\n	<property name=\"PartySharedKillRange\"			value=\"100\"/>				<!-- The distance you must be within to receive party shared kill xp and quest party kill objective credit. -->\n	<property name=\"PlayerKillingMode\"				value=\"3\" />				<!-- Player Killing Settings (0 = No Killing, 1 = Kill Allies Only, 2 = Kill Strangers Only, 3 = Kill Everyone) -->\n\n	<!-- Land claim options -->\n	<property name=\"LandClaimCount\"					value=\"1\"/>					<!-- Maximum allowed land claims per player. -->\n	<property name=\"LandClaimSize\"					value=\"41\"/>				<!-- Size in blocks that is protected by a keystone -->\n	<property name=\"LandClaimDeadZone\"				value=\"30\"/>				<!-- Keystones must be this many blocks apart (unless you are friends with the other player) -->\n	<property name=\"LandClaimExpiryTime\"			value=\"7\"/>					<!-- The number of days a player can be offline before their claims expire and are no longer protected -->\n	<property name=\"LandClaimDecayMode\"				value=\"0\"/>					<!-- Controls how offline players land claims decay. 0=Slow (Linear) , 1=Fast (Exponential), 2=None (Full protection until claim is expired). -->\n	<property name=\"LandClaimOnlineDurabilityModifier\"	value=\"4\"/>				<!-- How much protected claim area block hardness is increased when a player is online. 0 means infinite (no damage will ever be taken). Default is 4x -->\n	<property name=\"LandClaimOfflineDurabilityModifier\"	value=\"4\"/>				<!-- How much protected claim area block hardness is increased when a player is offline. 0 means infinite (no damage will ever be taken). Default is 4x -->\n	<property name=\"LandClaimOfflineDelay\"			value=\"0\"/>					<!-- The number of minutes after a player logs out that the land claim area hardness transitions from online to offline. Default is 0 -->\n\n\n	<!-- There are several game settings that you cannot change when starting a new game.\n	You can use console commands to change at least some of them ingame.\n	setgamepref BedrollDeadZoneSize 30 -->\n</ServerSettings>', 0, 0, 0, '<?xml version=\"1.0\" encoding=\"utf-16\" standalone=\"yes\"?>\r\n<values>\r\n  <add key=\"__TCA:CREATED_BY\" value=\"3\" type=\"System.Int32,mscorlib\" />\r\n  <add key=\"__TCA:CREATED_ON\" value=\"2018-04-19 18:47:48\" type=\"System.DateTime,mscorlib\" />\r\n  <add key=\"__TCA:NOTES\" value=\"\" type=\"System.String,mscorlib\" />\r\n  <add key=\"__TCA:Encoding\" value=\"65001\" type=\"System.Int32,mscorlib\" />\r\n  <add key=\"__TCA:MODIFIED_BY\" value=\"3\" type=\"System.Int32,mscorlib\" />\r\n  <add key=\"__TCA:MODIFIED_ON\" value=\"2020-04-07 00:46:00\" type=\"System.DateTime,mscorlib\" />\r\n</values>', 0, 1),
(94, 1, 0, 'mods\\deathmatch\\mtaserver.conf', 'Main configuration file', NULL, NULL, 0, '<config>\n\n    <!-- This parameter specifies the name the server will be visible as in the ingame server browser \n         and on Game-Monitor. It is a required parameter. -->\n	<servername>$[HostName]</servername>\n    \n    <!-- This parameter specifies the contact email addresses for the owner(s) of this server.\n         The email addresses will not be publicly available, and only used by MTA administrators\n         to contact the server owner.\n         Note: Missing or incorrect owner_email_address can affect visibility in the master server list.\n         Values: Comma separated list of email addresses -->\n    <owner_email_address></owner_email_address>\n\n    <!-- ONLY USE THIS PARAMETER IF YOU ARE SURE OF WHAT YOU ARE DOING - it is generally only\n         needed for professional servers and should be left at the default value otherwise.\n         This parameter specifies the IP to use for servers that have multiple IP addresses. If set\n         to auto, it will automatically detect and use the server\'s standard local IP address.\n         Values: auto or x.x.x.x ; default value: auto -->\n    <!-- SERVERIP SHOULD BE LEFT SET TO auto UNLESS YOU ARE SURE OF WHAT YOU ARE DOING -->\n	<serverip>$[Service.IpAddress]</serverip>\n    <!-- WARNING: SETTING serverip AND THEN ASKING FOR SUPPORT CAN CAUSE DEATH OR INJURY -->\n\n    <!-- This parameter specifies the UDP port on which the server will be accepting incoming player\n         connections; default value: 22003. It is a required parameter. -->\n	<serverport>$[Service.GamePort]</serverport> \n    \n    <!-- This parameter specifies the number of maximum player slots available on the server; default\n         value: 32. It is a required parameter. -->\n	<maxplayers>$[Service.Slots]</maxplayers>\n        \n    <!-- This parameter specifies the TCP port on which the server will be accepting incoming http\n         connections. It can be set to the same value as <serverport>. It is a required parameter\n         if <httpserver> is set to 1. -->\n	<httpport>$[Service.CustomPort1]</httpport>\n    \n    <!-- If set, this parameter specifies the external URL from which clients will be able to download\n         needed resources ingame. If not set (or the external URL files are incorrect), resource downloads\n         are switched to the internal http server. -->\n    <httpdownloadurl></httpdownloadurl>\n\n    <!-- This parameter limits the number of http connections each client can make. Depending on the type\n         of http server that is used, a lower figure may reduce download timeouts.\n         Available range: 1 to 8. -->\n    <httpmaxconnectionsperclient>5</httpmaxconnectionsperclient>\n\n    <!-- This parameter limits the number http connections that an IP can initiate over a short period of time.\n         Available range: 1 to 100. default value: 20 -->\n    <httpdosthreshold>20</httpdosthreshold>\n\n    <!-- This parameter lists the IP addresses that are to be excluded from http dos threshold limits.\n         e.g. 88.11.22.33,101.2.3.4 -->\n    <http_dos_exclude></http_dos_exclude>\n\n    <!-- By default, the server will block the use of locally customized gta3.img player skins\n         This setting can be used to allow such mods. Not recommended for competitive servers.\n         Values: none, peds ; default value: none -->\n    <allow_gta3_img_mods>none</allow_gta3_img_mods>\n\n    <!-- By default, the server will block the use of customized GTA:SA data files. -->\n    <!-- To allow specific client files, add one or more of the following: -->\n    <!-- <client_file name=\"data/carmods.dat\" verify=\"0\"/> -->\n\n    <!-- Comma separated list of disabled anti-cheats.\n         For details see http://wiki.multitheftauto.com/wiki/Anti-cheat_guide\n         e.g. To disable anti-cheat #2 and #3, use: 2,3 -->\n    <disableac></disableac>\n\n    <!-- Comma separated list of enabled special detections.\n         A special detection is a type of anti-cheat for (usually) harmless game modifications.\n         Competitive servers may wish to enable certain special detections, but most servers should leave this setting on its default.\n         For details and SD codes, see http://wiki.multitheftauto.com/wiki/Anti-cheat_guide\n         Available values: special detection (SD) codes ; default value: 31,32 (e.g. enables special detections #31 and #32) -->\n    <enablesd>31,32</enablesd>\n\n    <!-- Minimum client version. Clients with a lower version will not be allowed to connect. After\n         disconnection, clients will be given an opportunity to download an update.\n         If left blank, this setting is disabled and there are no restrictions on who can connect.\n         Version numbers are described here: http://wiki.multitheftauto.com/wiki/GetPlayerVersion\n         and look like this: 1.1.0-9.03100.0 \n         Note that this setting only determines if the client should be prompted to update. The actual\n         build number they receive will be the highest available. See: http://nightly.mtasa.com/ver  -->\n    <minclientversion></minclientversion>\n\n    <!-- This parameter specifies if/when the <minclientversion> setting is automatically updated.\n         Keeping <minclientversion> updated can help reduce cheating.\n         Note: The instant setting (2) is only recommended for competitive servers.\n         Values: 0 - disabled, 1 - enabled (delayed by a few days), 2 - enabled (instant) ; default value: 1.  -->\n    <minclientversion_auto_update>1</minclientversion_auto_update>\n\n    <!-- Recommended client version. When connecting, if clients have a lower version, they will be given\n         the option to download an update. If left blank, this setting is disabled.\n         Note that this setting only determines if the client should be prompted to update. The actual\n         build number they receive will be the highest available. See: http://nightly.mtasa.com/ver  -->\n    <recommendedclientversion></recommendedclientversion>\n\n    <!-- This parameter can be used to make the server report to Game-Monitor master servers, allowing it to\n         be visible in the ingame server browser. An additional UDP port needs to be available for this to\n         work (value from <serverport> + 123 , so on a default <serverport> value 22003 the right port\n         will be 22126 ). Available values: 0 - disabled , 1 - enabled. Optional parameter, defaults to 0. -->\n    <ase>1</ase>\n\n    <!-- This parameter allows you to disable LAN broadcasting. --> \n    <donotbroadcastlan>0</donotbroadcastlan>\n\n    <!-- If set, players will have to provide a password specified below, before they can connect to the\n         server. If left blank, server doesn\'t require a password from them. -->\n	<password>$[PrivatePassword]</password> \n\n    <!-- This parameter reduces the server\'s bandwidth usage by using various optimizations.\n         Values: none, medium or maximum ; default value: medium -->\n    <bandwidth_reduction>medium</bandwidth_reduction> \n\n    <!-- The following ???_sync_interval parameters determine the time in milliseconds between certain\n         network packets being sent. More information on how each settings works is available here:\n         http://wiki.multitheftauto.com/wiki/Server_mtaserver.conf -->\n    <!-- Player sync interval. Default: 100 -->\n    <player_sync_interval>100</player_sync_interval>\n    <!-- Lightweight (player) sync interval. Used when players are far apart. Default: 1500 -->\n    <lightweight_sync_interval>1500</lightweight_sync_interval> \n    <!-- Camera sync interval. Default: 500 -->\n    <camera_sync_interval>500</camera_sync_interval> \n    <!-- Ped sync interval. Default: 400 -->\n    <ped_sync_interval>400</ped_sync_interval> \n    <!-- Unoccupied_vehicle sync interval. Default: 400 -->\n    <unoccupied_vehicle_sync_interval>400</unoccupied_vehicle_sync_interval> \n    <!-- Keysync mouse rotation sync interval. For limiting key sync packets due to mouse movement. Default: 100 -->\n    <keysync_mouse_sync_interval>100</keysync_mouse_sync_interval> \n    <!-- Keysync analog movement sync interval. For limiting key sync packets due to joystick movement. Default: 100 -->\n    <keysync_analog_sync_interval>100</keysync_analog_sync_interval> \n\n    <!-- This parameter can improve the reliability of shots when using certain weapons. However, it uses more bandwidth.\n         Values: 0 - disabled , 1 - enabled ; default value: 1. -->\n    <bullet_sync>1</bullet_sync> \n\n    <!-- This parameter sets the amount of extrapolation that clients will apply to remote vehicles. This can reduce\n         some of the latency induced location disparency by predicting where the remote vehicles will probably be.\n         Depending on the gamemode, an incorrect prediction may have a negative effect. Therefore this setting\n         should be considered experimental.\n         Available range: 0 to 100.  Default - 0 -->\n    <vehext_percent>0</vehext_percent>\n\n    <!-- This parameter places a limit on how much time (in milliseconds) the vehicle extrapolation will attempt to\n         compensate for.\n         Only relevant if <vehext_percent> is greater than zero.\n         Available range: 50 to 500.  Default - 150 -->\n    <vehext_ping_limit>150</vehext_ping_limit>\n\n    <!-- This parameter can reduce the delay of player actions appearing on remote clients by 2 frames (approx 50ms).\n         Due to the impact this may have on shot lag compensation, it should be considered experimental.\n         Values: 0 - disabled , 1 - enabled ; default value: 0. -->\n    <latency_reduction>0</latency_reduction>\n\n    <!-- Specifies the location and file name of this servers unique private key.\n         This is used to prevent private files saved on the client from being read by other servers.\n         More infomation about client private files can be found here: http://wiki.multitheftauto.com/wiki/Filepath\n         Keep a backup of this file in a safe place. Default value: server-id.keys-->\n    <idfile>server-id.keys</idfile> \n\n    <!-- Specifies the location and name of the main server log file. If left blank, server won\'t be saving this file. -->\n    <logfile>logs/server.log</logfile>\n\n    <!-- As well as the main log file, login successes and failures are logged here for easy reviewing of security issues.\n         If left blank, this file is not used -->\n    <authfile>logs/server_auth.log</authfile>\n\n    <!-- Specifies the location and name of the file used to log database queries.\n         The server command \'debugdb\' sets the amount of logging. -->\n    <dbfile>logs/db.log</dbfile>\n\n    <!-- Specifies the location and name of the file used to log loadstring function calls.\n         If left blank or not set, no logging is done. -->\n    <!-- <loadstringfile>logs/loadstring.log</loadstringfile> -->\n    \n    <!-- This parameter specifies the location and name of the Access Control List settings file. If left\n         blank, server will use acl.xml file, located in the same folder as this configuration file. -->\n    <acl>acl.xml</acl> \n\n    <!-- Specifies the location and name of the debugscript log file. If left blank, server won\'t be saving this file. -->\n    <scriptdebuglogfile>logs/scripts.log</scriptdebuglogfile>\n\n    <!-- Specifies the level of the debugscript log file. Available values: 0, 1, 2, 3. When not set, defaults to 0. -->\n    <scriptdebugloglevel>0</scriptdebugloglevel>\n\n    <!-- Specifies the level of the html debug. Available values: 0, 1, 2, 3. When not set, defaults to 0. -->\n    <htmldebuglevel>0</htmldebuglevel>\n\n    <!-- Specifies whether or not duplicate log lines should be filtered. Available values: 0 or 1, defaults to 1. -->\n    <filter_duplicate_log_lines>1</filter_duplicate_log_lines>\n\n    <!-- Specifies the frame rate limit that will be applied to connecting clients.\n         Available range: 25 to 100. Default: 36. -->\n    <fpslimit>36</fpslimit>\n\n    <!-- This parameter specifies whether or not to enable player voice chat in-game\n         Values: 0 - disabled , 1 - enabled -->\n    <voice>0</voice>\n    \n    <!-- This parameter specifies the sample rate for voice chat.  \'voice\' parameter must be set to 1 for this to be effective.\n         Higher settings use more bandwidth and increase the sampling quality of voice chat\n         Values: 0 - Narrowband (8kHz), 1 - Wideband (16kHz), 2 - Ultrawideband (32kHz).  Default - 1 -->\n    <voice_samplerate>1</voice_samplerate>\n    \n    <!-- This parameter specifies the voice quality for voice chat.  \'voice\' parameter must be set to 1 for this to be effective.\n         Higher settings use more bandwidth and increase the the overall quality of voice chat\n         Available range: 0 to 10.  Default - 4 -->\n    <voice_quality>4</voice_quality>\n\n    <!-- Specifies the voice bitrate, in bps. This optional parameter overrides the previous two settings. \n         If not set, MTA handles this automatically.  Use with care. -->\n    <!-- <voice_bitrate>24600</voice_bitrate> -->\n\n    <!-- This parameter specifies the path to use for a basic backup of some server files.\n         Note that basic backups are only made during server startup. Default value: backups -->\n    <backup_path>backups</backup_path>\n\n    <!-- This parameter specifies the number of days between each basic backup.\n         Backups are only made during server startup, so the actual interval may be much longer.\n         Setting backup_interval to 0 will disable backups\n         Available range: 0 to 30.  Default - 3 -->\n    <backup_interval>3</backup_interval>\n\n    <!-- This parameter specifies the maximum number of backup copies to keep.\n         Setting backup_copies to 0 will disable backups\n         Available range: 0 to 100.  Default - 5 -->\n    <backup_copies>5</backup_copies>\n\n    <!-- This parameter specifies when the internal sqlite databases should be defragmented.\n         http://www.sqlite.org/lang_vacuum.html\n         Values: 0 - Never, 1 - On server start only after basic backup, 2 - On server start always.  Default - 1 -->\n    <compact_internal_databases>1</compact_internal_databases>\n\n    <!-- This parameter specifies whether server crash dump files should be sent to MTA HQ.\n         Values: 0 - Off, 1 - On. Default - 1 -->\n    <crash_dump_upload>1</crash_dump_upload>\n\n    <!-- This parameter lists the ACL groups that are protected by serial authorization.\n         Login attempts to a protected account from a second serial are blocked until the serial is manually authorized via\n         the authserial command.\n         For more info see: https://mtasa.com/authserial\n         Note: This is security critical feature and disabling auth_serial_groups can affect visibility in the master server list.\n         Values: Comma separated list of ACL groups.  Default - Admin -->\n    <auth_serial_groups>Admin</auth_serial_groups>\n\n    <!-- This parameter specifies if the authorized serial login checks should also apply to the http interface.\n         Protected account login attempts to the http interface will only succeed if the IP address matches one\n         recently used by the account holder in-game\n         For more info see: https://mtasa.com/authserialhttp\n         Note: This is security critical feature and disabling auth_serial_http can affect visibility in the master server list.\n         Values: 0 - Off, 1 - Enabled.  Default - 1 -->\n    <auth_serial_http>1</auth_serial_http>\n\n    <!-- This parameter specifies which IP addresses should always pass auth_serial_http checks.\n         Values: Comma separated list of IP addresses -->\n    <auth_serial_http_ip_exceptions>127.0.0.1</auth_serial_http_ip_exceptions>\n\n    <!-- This parameter specifies if extra security measures are applied to resources which use dbConnect with mysql.\n         The extra measures are:\n           - Script files cannot be accessed with fileOpen()\n           - meta.xml is read only\n         *NOTE* This only protects resources which use dbConnect with mysql\n         Values: 0 - Off, 1 - Enabled.  Default - 1 -->\n    <database_credentials_protection>1</database_credentials_protection>\n\n    <!-- Specifies the module(s) which are loaded with the server. To load several modules, add more <module>\n         parameter(s). Optional parameter. -->\n    <!-- <module src=\"sample_win32.dll\"/> -->\n    <!-- <module src=\"sample_linux.so\"/> -->\n    \n    <!-- Specifies resources that are loaded when the server starts and/or which are protected from being stopped.\n         To specify several resources, add more <resource> parameter(s). -->\n\n    <resource src=\"admin\" startup=\"1\" protected=\"0\"/>\n    <resource src=\"defaultstats\" startup=\"1\" protected=\"0\"/>\n    <resource src=\"helpmanager\" startup=\"1\" protected=\"0\"/>\n    <resource src=\"joinquit\" startup=\"1\" protected=\"0\"/>\n    <resource src=\"mapcycler\" startup=\"1\" protected=\"0\"/>\n    <resource src=\"mapmanager\" startup=\"1\" protected=\"0\"/>\n    <resource src=\"parachute\" startup=\"1\" protected=\"0\"/>\n    <resource src=\"performancebrowser\" startup=\"1\" protected=\"0\"/>\n    <resource src=\"reload\" startup=\"1\" protected=\"0\"/>\n    <resource src=\"resourcebrowser\" startup=\"1\" protected=\"1\" default=\"true\"/>\n    <resource src=\"resourcemanager\" startup=\"1\" protected=\"1\"/>\n    <resource src=\"scoreboard\" startup=\"1\" protected=\"0\"/>\n    <resource src=\"spawnmanager\" startup=\"1\" protected=\"0\"/>\n    <resource src=\"voice\" startup=\"1\" protected=\"0\" />\n    <resource src=\"votemanager\" startup=\"1\" protected=\"0\"/>\n    <resource src=\"webadmin\" startup=\"1\" protected=\"0\"/>\n    \n    <!-- play is the default freeroam gamemode. Remove the following line to prevent it from starting. -->       \n    <resource src=\"play\" startup=\"1\" protected=\"0\"/>\n    \n\n</config>', 0, 0, 0, '<?xml version=\"1.0\" encoding=\"utf-16\" standalone=\"yes\"?>\r\n<values>\r\n  <add key=\"__TCA:CREATED_BY\" value=\"3\" type=\"System.Int32,mscorlib\" />\r\n  <add key=\"__TCA:CREATED_ON\" value=\"2012-06-28 23:48:24\" type=\"System.DateTime,mscorlib\" />\r\n  <add key=\"__TCA:NOTES\" value=\"\" type=\"System.String,mscorlib\" />\r\n  <add key=\"__TCA:MODIFIED_BY\" value=\"3\" type=\"System.Int32,mscorlib\" />\r\n  <add key=\"__TCA:MODIFIED_ON\" value=\"2019-08-09 01:41:03\" type=\"System.DateTime,mscorlib\" />\r\n  <add key=\"__TCA:Encoding\" value=\"0\" type=\"System.Int32,mscorlib\" />\r\n</values>', 0, 1),
(125, 1, 0, 'Scripts/Linux/Servers/myserver/Server/Commands.dat', '', NULL, NULL, 0, 'name myserver\r\npve\r\nmode easy\r\nmap Chernarus\r\nport $[Service.CustomPort1]\r\nmaxplayers $[Service.Slots]\r\nbind $[Service.IpAddress]\r\nloadout 425/200/179/2/76/16/95/95/95/121/13/13/14/14\r\nWelcome Welcome to Chernarus! We hope you enjoy your stay! :3\r\n', 0, 0, 0, '<?xml version=\"1.0\" encoding=\"utf-16\" standalone=\"yes\"?>\n<values>\n  <add key=\"__TCA:NOTES\" value=\"\" type=\"System.String,mscorlib\" />\n  <add key=\"__TCA:CREATED_BY\" value=\"3\" type=\"System.Int32,mscorlib\" />\n  <add key=\"__TCA:CREATED_ON\" value=\"2018-04-10 16:01:57\" type=\"System.DateTime,mscorlib\" />\n  <add key=\"__TCA:MODIFIED_BY\" value=\"3\" type=\"System.Int32,mscorlib\" />\n  <add key=\"__TCA:MODIFIED_ON\" value=\"2019-02-18 22:50:58\" type=\"System.DateTime,mscorlib\" />\n  <add key=\"__TCA:Encoding\" value=\"0\" type=\"System.Int32,mscorlib\" />\n</values>', 0, NULL);
INSERT INTO `game_config_files` (`game_id`, `file_id`, `mod_id`, `relative_path`, `description`, `enable_text_editor_reseller`, `enable_text_editor_user`, `view_order`, `template`, `enable_config_editor`, `read_values`, `hide_path`, `app_data`, `hide_read_failure`, `update_automatically`) VALUES
(135, 1, 0, 'serverconfig.xml', 'Main configuration file', NULL, NULL, 0, '<?xml version=\"1.0\"?>\n<ServerSettings>\n  <property name=\"ServerPort\" 				value=\"$[Service.GamePort]\"/>				<!-- Port you want the server to listen on. -->\n  <property name=\"ServerIsPublic\" 			value=\"$[IsPublicBoolean]\"/>				<!-- Should this server register to master server -->\n  <property name=\"ServerName\" 				value=\"$[HostName]\"/>		<!-- Whatever you want the name to be. -->\n  <property name=\"ServerPassword\" 			value=\"$[PrivatePassword]\"/>					<!-- Password to gain entry to the server -->\n  <property name=\"ServerMaxPlayerCount\" 	value=\"$[Service.Slots]\"/>					<!-- Maximum Concurrent Players -->\n  <property name=\"ServerReservedSlots\"		value=\"0\"/>					<!-- Out of the MaxPlayerCount this many slots can only be used by players with a specific permission level -->\n  <property name=\"ServerReservedSlotsPermission\"	value=\"100\"/>		<!-- Required permission level to use reserved slots above -->\n  <property name=\"ServerAdminSlots\"			value=\"0\"/>					<!-- This many admins can still join even if the server has reached MaxPlayerCount -->\n  <property name=\"ServerAdminSlotsPermission\"	value=\"0\"/>				<!-- Required permission level to use the admin slots above -->\n\n  <property name=\"ServerDescription\" 		value=\"A 7 Days to Die server\"/> <!-- Whatever you want the description to be. -->\n  <property name=\"ServerWebsiteURL\" 		value=\"\"/>					<!-- Website URL for the server -->\n  <property name=\"ServerDisabledNetworkProtocols\"	value=\"UNET\"/>			<!-- Networking protocols that should not be used. Separated by comma. Possible values: UNET, RakNet, SteamNetworking. Ex: \"unet,steamnetworking\" -->\n\n  <property name=\"GameWorld\" 				value=\"Navezgane\"/>			<!-- Navezgane, Random Gen -->\n  <property name=\"GameName\" 				value=\"My Game\"/>			<!-- Whatever you want the game name to be THIS CONTROLS THE RANDOM GENERATION SEED -->\n  <property name=\"GameDifficulty\" 			value=\"2\"/>  				<!-- 0 - 5, 0=easiest, 5=hardest -->\n  <property name=\"GameMode\"					value=\"GameModeSurvivalMP\"/>	<!-- GameModeSurvivalMP, GameModeSurvivalSP (MP has land protection) -->\n\n  <property name=\"BuildCreate\"				value=\"false\" />				<!-- cheat mode on/off -->\n  <property name=\"DayNightLength\"			value=\"60\" />				<!-- real time minutes per in game day: 60 minutes -->\n  <property name=\"DayLightLength\"			value=\"18\" />				<!-- in game hours the sun shines per day: 18 hours day light per day-->\n  <property name=\"PlayerKillingMode\"		value=\"3\" />				<!-- Player Killing Settings (0 = No Killing, 1 = Kill Allies Only, 2 = Kill Strangers Only, 3 = Kill Everyone) -->\n  <property name=\"PersistentPlayerProfiles\"	value=\"false\" />			<!-- If disabled a player can join with any selected profile. If true they will join with the last profile they joined with -->\n\n  <property name=\"PlayerSafeZoneLevel\"	    value=\"5\" />				<!-- If a player is less or equal this level he will create a safe zone (no enemies) when spawned -->\n  <property name=\"PlayerSafeZoneHours\"	    value=\"5\" />				<!-- Hours in world time this safe zone exists -->\n\n  <property name=\"ControlPanelEnabled\"	 	value=\"false\"/>				<!-- Enable/Disable the control panel -->\n  <property name=\"ControlPanelPort\" 		value=\"$[Service.CustomPort1]\"/>				<!-- Port of the control panel webpage -->\n  <property name=\"ControlPanelPassword\" 	value=\"CHANGEME\"/>			<!-- Password to gain entry to the control panel -->\n\n  <property name=\"TelnetEnabled\"	 		value=\"true\"/>				<!-- Enable/Disable the telnet -->\n  <property name=\"TelnetPort\" 				value=\"$[Service.RConPort]\"/>				<!-- Port of the telnet server -->\n  <property name=\"TelnetPassword\" 			value=\"$[RConPassword]\"/>			<!-- Password to gain entry to telnet interface -->\n\n  <property name=\"AdminFileName\"			value=\"serveradmin.xml\"/>	<!-- Server admin file name -->\n\n  <property name=\"DropOnDeath\"				value=\"0\" />				<!-- 0 = everything, 1 = toolbelt only, 2 = backpack only, 3 = delete all -->\n  <property name=\"DropOnQuit\"				value=\"0\" />				<!-- 0 = nothing, 1 = everything, 2 = toolbelt only, 3 = backpack only -->\n\n  <property name=\"BloodMoonEnemyCount\"		value=\"8\" />				<!-- The number of zombies spawned during blood moons per player. -->\n  <property name=\"EnemySpawnMode\"			value=\"true\" />				<!-- Enable/Disable enemy spawning -->\n  <property name=\"EnemyDifficulty\"			value=\"0\" />				<!-- 0 = Normal, 1 = Feral -->\n\n  <property name=\"LootAbundance\" 			value=\"100\" /> 				<!-- percentage in whole numbers -->\n  <property name=\"LootRespawnDays\"		 	value=\"30\" /> 				<!-- days in whole numbers -->\n\n  <property name=\"LandClaimSize\" 						value=\"41\"/>	<!-- Size in blocks that is protected by a keystone -->\n  <property name=\"LandClaimDeadZone\" 					value=\"30\"/>	<!-- Keystones must be this many blocks apart (unless you are friends with the other player) -->\n  <property name=\"LandClaimExpiryTime\" 					value=\"3\"/>		<!-- The number of days a player can be offline before their claims expire and are no longer protected -->\n  <property name=\"LandClaimDecayMode\" 					value=\"0\"/>		<!-- Controls how offline players land claims decay. All claims have full protection for the first 24hrs. 0=Linear, 1=Exponential, 2=Full protection until claim is expired. -->\n  <property name=\"LandClaimOnlineDurabilityModifier\" 	value=\"4\"/>		<!-- How much protected claim area block hardness is increased when a player is online. 0 means infinite (no damage will ever be taken). Default is 32x -->\n  <property name=\"LandClaimOfflineDurabilityModifier\" 	value=\"4\"/>		<!-- How much protected claim area block hardness is increased when a player is offline. 0 means infinite (no damage will ever be taken). Default is 32x -->\n  <property name=\"AirDropFrequency\"						value=\"72\"/>    <!-- How often airdrop occur in game-hours, 0 == never -->\n  <property name=\"AirDropMarker\"						value=\"false\"/>	<!-- Sets if a marker is added to map/compass for air drops. -->\n  <property name=\"MaxSpawnedZombies\"					value=\"60\"/>    <!-- Making this number too large (more than about 80) may cause servers to run at poor framerates which will effect lag and play quality for clients. -->\n\n  <!--\n  If your server has a large number of players you can increase this limit to add more wildlife. Animals don\'t consume as much CPU as zombies.\n  NOTE: That this doesn\'t cause more animals to spawn arbitrarily: The biome spawning system only spawns a certain number of animals in a given\n  area, but if you have lots of players that are all spread out then you may be hitting the limit and can increase it.\n  -->\n  <property name=\"MaxSpawnedAnimals\"					value=\"50\"/>\n  <property name=\"EACEnabled\"							value=\"true\"/>  <!-- Enables/Disables EasyAntiCheat -->\n  <property name=\"HideCommandExecutionLog\"				value=\"0\"/>		<!-- Hide logging of command execution. 0 = show everything, 1 = hide only from Telnet/ControlPanel, 2 = also hide from remote game clients, 3 = hide everything -->\n  <property name=\"MaxUncoveredMapChunksPerPlayer\"		value=\"131072\"/>	<!-- Override how many chunks can be uncovered on the ingame map by each player. Resulting max map file size limit per player is (x * 512 Bytes), uncovered area is (x * 256 m). Default 131072 means max 32 km can be uncovered at any time -->\n  <property name=\"BedrollDeadZoneSize\"		value=\"15\"/>	<!-- Size of bedroll deadzone, no zombies will spawn inside this area, and any cleared sleeper volumes that touch a bedroll deadzone will not spawn after they\'ve been cleared. -->\n\n  <!--property name=\"SaveGameFolder\"      value=\"absolute path\" /-->	<!-- use this to override the default save game path -->\n</ServerSettings>', 0, 0, 0, '<?xml version=\"1.0\" encoding=\"utf-16\" standalone=\"yes\"?>\r\n<values>\r\n  <add key=\"__TCA:CREATED_BY\" value=\"3\" type=\"System.Int32,mscorlib\" />\r\n  <add key=\"__TCA:CREATED_ON\" value=\"2018-04-19 18:47:48\" type=\"System.DateTime,mscorlib\" />\r\n  <add key=\"__TCA:NOTES\" value=\"\" type=\"System.String,mscorlib\" />\r\n  <add key=\"__TCA:Encoding\" value=\"0\" type=\"System.Int32,mscorlib\" />\r\n  <add key=\"__TCA:MODIFIED_BY\" value=\"3\" type=\"System.Int32,mscorlib\" />\r\n  <add key=\"__TCA:MODIFIED_ON\" value=\"2020-03-10 15:12:14\" type=\"System.DateTime,mscorlib\" />\r\n</values>', 0, 1),
